onCasting:
Set(_numMelfMeteorsToSend, Ask("Send how many meteors?", "0:Zero", "1:One", "2:Two"));

TellDmHowManyMeteorsAreComing();

if (spell_SpellSlotLevel > 3) 
  Set(_melfMeteorCount, 6 + 2 * (spell_SpellSlotLevel - 3));
else
  Set(_melfMeteorCount, 6);

if (_numMelfMeteorsToSend == 0)
  Casting = false;

if (_melfMeteorCount > 1) {
  Set(_numMelfMeteorsToSend, Ask($"Send how many meteors ({_melfMeteorCount} available)?", "1:One", "2:Two"));
  TellDmHowManyMeteorsAreComing();
}
else if (_melfMeteorCount == 1) {
  Set(_numMelfMeteorsToSend, 1);
  TellDmHowManyMeteorsAreComing();
}
else
{
  Set(_numMelfMeteorsToSend, 0);
  Dispel();
}

onCast:
if (_numMelfMeteorsToSend < _melfMeteorCount)
  _numMelfMeteorsToSend = _melfMeteorCount;

Offset(_melfMeteorCount, 0 - _numMelfMeteorsToSend);
Set(spell_DieStr, (_numMelfMeteorsToSend * 2).ToString() + "d6(fire)");
Set(_numMelfMeteorsToSend, 0);
if (_melfMeteorCount == 0) {
  TellDm($"{name} is out of meteors!");
  Dispel();
}

onPlayerAttacks:
if (AttackIsMelee && _thunderousSmiteWaitingOnHit) {
  AddDice("2d6(thunder)");
  AddTrailingEffects("Ravens;Spirals");
  AddDieRollEffects("PaladinSmite");
  AddDieRollMessage("Thunderous Smite");
}

onPlayerHitsTarget:
if (AttackIsMelee) {
  Set(_thunderousSmiteWaitingOnHit, false);
  if (TargetIsCreature) {
    TellDm($"Target must make a Strength saving throw or be pushed 10 feet away from {name} and knocked prone by this {spell_Name} spell.");
  }
  Dispel();
}

onPlayerAttacks:
if (AttackIsMelee && _wrathfulSmiteWaitingOnHit) {
  AddDice("1d6(psychic)");
  AddTrailingEffects("Ravens;Spirals");
  AddDieRollEffects("PaladinSmite");
  AddDieRollMessage("Wrathful Smite");
}

onPlayerHitsTarget:
if (AttackIsMelee) {
  Set(_wrathfulSmiteWaitingOnHit, false);
  if (TargetIsCreature) {
    TellDm($"Target must make a Wisdom saving throw or be frightened of {name} until the spell ends. As an action, the creature can make a Wisdom check against {name}'s spell save DC ({SpellSaveDC}) to steel its resolve and end this {spell_Name} spell.");
  }
}
